pre.code:
#include <algorithm>
#include <iostream>
#include <vector>
using namespace std;

struct Edge {
    int from, to, cost;
    Edge(int f, int t, int c): from(f), to(t), cost(c) {}
};

bool cmp(Edge x, Edge y)
{
    return x.cost < y.cost;
}

const int maxn = 505;
int n, m, father[maxn], ans = 0;
vector<Edge> edge;

int find(int x)
{
    return x == father[x] ? x : father[x] = find(father[x]);
}

int main()
{
    for (int i = 1; i < maxn; ++i) {
        father[i] = i;
    }
    cin >> n >> m;
    for (int i = 1; i <= m; ++i) {
        for (int j = 1, c; j <= m; ++j) {
            cin >> c;
            if (c == 0) continue;
            edge.push_back(Edge(i, j, c));
        }
    }
    sort(edge.begin(), edge.end(), cmp);
    for (size_t i = 0; i < edge.size(); ++i) {
        int x = find(edge[i].from), y = find(edge[i].to);
        int cost = edge[i].cost;
        if (x == y) continue;
        father[x] = y;
        ans += cost > n ? n : cost;
    }
    for (int i = 1; i <= m; ++i) {
        if (father[i] == i) ans += n;
    }
    cout << ans;
}
